$(function(){$("div.product-count a").click(function(t){t.preventDefault(),$("a.product-actual-count").text($(this).text()),i($(this).text())}),$("a#filter_button").click(function(t){t.preventDefault(),i($("a.product-actual-count").first().text())}),$("button.add-cart-button").click(function(t){t.preventDefault(),$.ajax({metod:"POST",headers:{"X-CSRF-TOKEN":$('meta[name="csrf-token"]').attr("content")},url:WELCOME_DATA.addToCart+$(this).data("id")}).done(function(){Swal.fire({title:"Sukces",text:"Produkt dodany do koszyka!",icon:"success",showCancelButton:"true",confirmButtonText:'<i class ="fas fa-cart-plus"></i> Przejdź do koszyka',cancelButtonText:'<i class="fas fa-shopping-bag"></i> Kontynuj zakupy'}).then(a=>{a.isConfirmed&&(window.location=WELCOME_DATA.listCart)})}).fail(function(a){Swal.fire("Oops...","Wystąpił błąd","error")})});function i(t){const a=$("form.sidebar-filter").serialize();$.ajax({method:"GET",url:"/",data:a+"&"+$.param({paginate:t})}).done(function(n){$("div#products-wrapper").empty(),$.each(n.data,function(s,e){const o='<div class="col-6 col-md-6 col-lg-4 mb-3"> <div class="card h-100 border-0">  <div class="card-img-top">     <img src="'+c(e)+'" class="img-fluid mx-auto d-block" alt="Card image cap">     </div>  <div class="card-body text-center">         <h4 class="card-title">'+e.name+'         </h4>     <h5 class="card-price small">     <i>PLN'+e.price+'</i>     </h5>    </div>     <button class="btn btn-success btn-sm add-cart-button"'+getDisabled()+' data-id="'+e.id+'">         <i class="fas fa-cart-plus"></i> Dodaj do koszyka     </button> </div></div>';$("div#products-wrapper").append(o)})})}function c(t){return t.image_path?WELCOME_DATA.storagePath+t.image_path:WELCOME_DATA.defaultImage}});
