$(function(){$("div.product-count a").click(function(t){t.preventDefault(),$("a.product-actual-count").text($(this).text()),e($(this).text())}),$("a#filter_button").click(function(t){t.preventDefault(),e($("a.product-actual-count").first().text())});function e(t){const r=$("form.sidebar-filter").serialize();$.ajax({method:"GET",url:"/",data:r+"&"+$.param({paginate:t})}).done(function(i){$("div#products-wrapper").empty(),$.each(i.data,function(n,a){const d='<div class="col-6 col-md-6 col-lg-4 mb-3"> <div class="card h-100 border-0">  <div class="card-img-top">     <img src="'+c(a)+'" class="img-fluid mx-auto d-block" alt="Card image cap">     </div>  <div class="card-body text-center">         <h4 class="card-title">'+a.name+'         </h4>     <h5 class="card-price small">     <i>PLN'+a.price+"</i>     </h5> </div> </div></div>";$("div#products-wrapper").append(d)})}).fail(function(i){alert("ERROR")})}function c(t){return t.image_path?storagePath+t.image_path:defaultImage}});
