$(function(){$("div.product-count a").click(function(){$("a.product-actual-count").text($(this).text())}),$("a#filter_button").click(function(){const a=$("form.sidebar-filter").serialize();$.ajax({method:"GET",url:"/",data:a}).done(function(i){$("div#products-wrapper").empty(),$.each(i.data,function(d,t){const e='<div class="col-6 col-md-6 col-lg-4 mb-3"> <div class="card h-100 border-0">  <div class="card-img-top">     <img src="'+c(t)+'" class="img-fluid mx-auto d-block" alt="Card image cap">     </div>  <div class="card-body text-center">         <h4 class="card-title">'+t.name+'         </h4>     <h5 class="card-price small">     <i>PLN'+t.price+"</i>     </h5> </div> </div></div>";$("div#products-wrapper").append(e)})}).fail(function(i){alert("ERROR")})});function c(a){return a.image_path?storagePath+a.image_path:defaultImage}});
